mapper.update:
UPDATE "BOARD" SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT NVL(
	(SELECT BOARD_NO 
	FROM "BOARD"
	WHERE BOARD_CODE = #{boardCode}
	AND BOARD_NO < #{boardNo}
	ORDER BY BOARD_NO DESC
	FETCH FIRST 1 ROW ONLY), -1
)AS BOARD_NO 
FROM "BOARD"
WHERE BOARD_CODE = #{boardCode}
ORDER BY BOARD_NO DESC
FETCH FIRST 1 ROW ONLY


SELECT NVL(
	(SELECT BOARD_NO 
	FROM "BOARD"
	WHERE BOARD_CODE = #{boardCode}
	AND BOARD_NO > #{boardNo}
	ORDER BY BOARD_NO
	FETCH FIRST 1 ROW ONLY), -1
) AS BOARD_NO
FROM "BOARD"
WHERE BOARD_CODE = ${boardCode}
ORDER BY BOARD_NO
FETCH FIRST 1 ROW ONLY
