mapper.select:
SELECT COUNT(*) FROM "CART"
WHERE MEMBER_NO = #{memberNo}
AND SEED_NO = #{seedNo}

mapper.update:
UPDATE "CART" SET
COUNT = (SELECT COUNT FROM "CART"
		WHERE MEMBER_NO = #{memberNo}
		AND SEED_NO = #{seedNo}) + 1
WHERE MEMBER_NO = #{memberNo}
AND SEED_NO = #{seedNo}

mapper.select:
SELECT COUNT(*) FROM "CART"
WHERE MEMBER_NO = #{memberNo}
AND SEED_NO = #{seedNo}

mapper.update:
UPDATE "CART" SET
COUNT = (SELECT COUNT FROM "CART"
		WHERE MEMBER_NO = #{memberNo}
		AND SEED_NO = #{seedNo}) + #{count}
WHERE MEMBER_NO = #{memberNo}
AND SEED_NO = #{seedNo}
